import java.util.*;

public class Main {
    static int numNodes;
    static Node[] nodes;
    static Node node1;
    static Node node2;
    static Node root;
    static Set<Node> nodeSet;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        numNodes = sc.nextInt();
        nodes = new Node[numNodes];
        for (int i = 0; i < numNodes; i++) {
            nodes[i] = new Node();
        }
        for (int i = 0; i < numNodes - 1; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            int w = sc.nextInt();
            node1 = nodes[u - 1];
            node2 = nodes[v - 1];
            node1.val = u;
            node2.val = v;
            node1.a.add(new Pair(node2, w));
            node2.a.add(new Pair(node1, w));
        }
        root = nodes[0];
        root.color = true;
        nodeSet = new HashSet<>();
        TraverseGraph(root, 0);
        for (int i = 0; i < numNodes; i++) {
            Node node = nodes[i];
            if (node.color) {
                System.out.println(1);
            } else {
                System.out.println(0);
            }
        }
    }

    static void TraverseGraph(Node node, int distance) {
        if (nodeSet.contains(node)) {
            return;
        } else {
            nodeSet.add(node);
        }
        for (Pair pair : node.a) {
            Node adjNode = pair.node;
            int dis = pair.dis;
            if ((distance + dis) % 2 == 0) {
                adjNode.color = root.color;
            } else {
                adjNode.color = !root.color;
            }
            TraverseGraph(adjNode, distance + dis);
        }
    }

    static class Node {
        int val;
        boolean color;
        List<Pair> a;

        public Node() {
            a = new ArrayList<>();
        }
    }

    static class Pair {
        Node node;
        int dis;

        public Pair(Node node, int dis) {
            this.node = node;
            this.dis = dis;
        }
    }
}
