import java.util.* ; public class codeforces_266_B { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int n = sc.nextInt ( ) ; int [ ] arr = new int [ n ] ; String s = sc.next ( ) ; for ( int i = 0 ; i < n ; i ++ ) { arr [ i ] = sc.nextInt ( ) ; } int pScore = 0 , sScore = 0 , pMaxScore = 0 , sMaxScore = 0 ; for ( int i = 0 ; i < n ; i ++ ) { if ( s.charAt ( i ) == 'A' ) { pScore += arr [ i ] ; if ( pScore > pMaxScore ) pMaxScore = pScore ; } else { pScore -= arr [ i ] ; } } for ( int i = n - 1 ; i >= 0 ; i -- ) { if ( s.charAt ( i ) == 'A' ) { sScore += arr [ i ] ; if ( sScore > sMaxScore ) sMaxScore = sScore ; } else { sScore -= arr [ i ] ; } } if ( pMaxScore > sMaxScore ) System.out.println ( pMaxScore ) ; else System.out.println ( sMaxScore ) ; } }

