import java.util.* ; import java.io.* ; public class codeforces_268_A { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int n = sc.nextInt ( ) ; int L = sc.nextInt ( ) ; int R = sc.nextInt ( ) ; int QL = sc.nextInt ( ) ; int QR = sc.nextInt ( ) ; int [ ] W = new int [ n ] ; for ( int i = 0 ; i < n ; i ++ ) { W [ i ] = sc.nextInt ( ) ; } long [ ] sum = new long [ n + 1 ] ; for ( int i = 1 ; i <= n ; i ++ ) { sum [ i ] = sum [ i - 1 ] + W [ i - 1 ] ; } long ans = QR * ( n - 1 ) + sum [ n ] * R ; for ( int i = 1 ; i <= n ; i ++ ) { long energy = L * sum [ i ] + R * ( sum [ n ] - sum [ i ] ) ; if ( i > n - i ) { energy += ( i - ( n - i ) - 1 ) * QL ; } else if ( n - i > i ) { energy += ( n - i - i - 1 ) * QR ; } ans = Math.min ( ans , energy ) ; } System.out.println ( ans ) ; } }

