import java.util.* ; import java.io.* ; public class atcoder_ABC152_D { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int H = sc.nextInt ( ) ; int W = sc.nextInt ( ) ; char [ ] [ ] c = new char [ H ] [ W ] ; for ( int i = 0 ; i < H ; i ++ ) { String s = sc.next ( ) ; for ( int j = 0 ; j < W ; j ++ ) { c [ i ] [ j ] = s.charAt ( j ) ; } } int [ ] [ ] dp = new int [ H ] [ W ] ; for ( int i = 0 ; i < H ; i ++ ) { for ( int j = 0 ; j < W ; j ++ ) { dp [ i ] [ j ] = Integer.MAX_VALUE ; } } for ( int i = 0 ; i < H ; i ++ ) { for ( int j = 0 ; j < W ; j ++ ) { if ( c [ i ] [ j ] == '#' ) { dp [ i ] [ j ] = 0 ; } } } for ( int i = 0 ; i < H ; i ++ ) { for ( int j = 0 ; j < W ; j ++ ) { if ( i > 0 ) { dp [ i ] [ j ] = Math.min ( dp [ i ] [ j ] , dp [ i - 1 ] [ j ] + 1 ) ; } if ( j > 0 ) { dp [ i ] [ j ] = Math.min ( dp [ i ] [ j ] , dp [ i ] [ j - 1 ] + 1 ) ; } } } for ( int i = H - 1 ; i >= 0 ; i -- ) { for ( int j = W - 1 ; j >= 0 ; j -- ) { if ( i < H - 1 ) { dp [ i ] [ j ] = Math.min ( dp [ i ] [ j ] , dp [ i + 1 ] [ j ] + 1 ) ; } if ( j < W - 1 ) { dp [ i ] [ j ] = Math.min ( dp [ i ] [ j ] , dp [ i ] [ j + 1 ] + 1 ) ; } } } int ans = 0 ; for ( int i = 0 ; i < H ; i ++ ) { for ( int j = 0 ; j < W ; j ++ ) { ans = Math.max ( ans , dp [ i ] [ j ] ) ; } } System.out.println ( ans ) ; } }

