import java.util.Scanner ; public class atcoder_ABC173_A { public static void main ( String [ ] args ) { Scanner stdIn = new Scanner ( System.in ) ; int N = stdIn.nextInt ( ) ; String [ ] S = new String [ N ] ; for ( int i = 0 ; i < N ; i ++ ) { S [ i ] = stdIn.next ( ) ; } String [ ] march = { "M" , "A" , "R" , "C" , "H" } ; int [ ] marchLis = new int [ 5 ] ; for ( int i = 0 ; i < 5 ; i ++ ) { marchLis [ i ] = 0 ; } for ( int i = 0 ; i < N ; i ++ ) { if ( S [ i ].charAt ( 0 ) == 'M' || S [ i ].charAt ( 0 ) == 'A' || S [ i ].charAt ( 0 ) == 'R' || S [ i ].charAt ( 0 ) == 'C' || S [ i ].charAt ( 0 ) == 'H' ) { marchLis [ march.indexOf ( S [ i ].toUpperCase ( ) ) ] ++ ; } } int ans = 0 ; for ( int x = 0 ; x < 5 ; x ++ ) { for ( int y = 0 ; y < 5 ; y ++ ) { for ( int z = 0 ; z < 5 ; z ++ ) { ans += marchLis [ x ] * marchLis [ y ] * marchLis [ z ] ; } } } System.out.println ( ans ) ; } }

