import java.util.* ; import java.io.* ; public class atcoder_ABC167_D { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int h = sc.nextInt ( ) ; int w = sc.nextInt ( ) ; int n = sc.nextInt ( ) ; int sr = sc.nextInt ( ) ; int scol = sc.nextInt ( ) ; String s = sc.next ( ) ; String t = sc.next ( ) ; boolean judge = false ; int sl = 0 , sr1 = 0 , su = 0 , sd = 0 ; int tl = 0 , tr = 0 , tu = 0 , td = 0 ; for ( int i = 0 ; i < n ; i ++ ) { if ( s.charAt ( i ) == 'L' ) { sl ++ ; if ( sl - tr - scol == 0 ) judge = true ; } else if ( s.charAt ( i ) == 'R' ) { sr1 ++ ; if ( scol + ( sr1 - tl ) == w + 1 ) judge = true ; } else if ( s.charAt ( i ) == 'U' ) { su ++ ; if ( su - td - sr == 0 ) judge = true ; } else if ( s.charAt ( i ) == 'D' ) { sd ++ ; if ( sr + ( sd - tu ) == h + 1 ) judge = true ; } if ( t.charAt ( i ) == 'L' ) { if ( sr1 - tl + scol != 1 ) tl ++ ; } else if ( t.charAt ( i ) == 'R' ) { if ( scol + ( tr - sl ) != w ) tr ++ ; } else if ( t.charAt ( i ) == 'U' ) { if ( sd - tu + sr != 1 ) tu ++ ; } else if ( t.charAt ( i ) == 'D' ) { if ( sr + ( td - su ) != h ) td ++ ; } } if ( judge ) System.out.println ( "NO" ) ; else System.out.println ( "YES" ) ; } }

