import java.util.Scanner ; import java.util.Vector ; public class codeforces_591_A { public static void main ( String args [ ] ) { Scanner sc = new Scanner ( System.in ) ; Vector < String > vec = new Vector < String > ( ) ; for ( int i = 0 ; i < 4 ; i ++ ) { vec.add ( sc.next ( ) ) ; } int black = 0 ; int white = 0 ; int flag = 0 ; for ( int i = 0 ; i < 3 ; i ++ ) { for ( int j = 0 ; j < 3 ; j ++ ) { if ( vec.get ( i ).charAt ( j ) == '#' ) black ++ ; if ( vec.get ( i ).charAt ( j + 1 ) == '#' ) black ++ ; if ( vec.get ( i + 1 ).charAt ( j ) == '#' ) black ++ ; if ( vec.get ( i + 1 ).charAt ( j + 1 ) == '#' ) black ++ ; if ( black == 0 || black == 4 ) { flag = 1 ; break ; } white = 4 - black ; if ( white == 0 || white == 4 ) { flag = 1 ; break ; } black = 0 ; white = 0 ; } if ( flag == 1 ) break ; } if ( flag == 1 ) System.out.println ( "YES" ) ; else System.out.println ( "NO" ) ; } }

