import java.io.* ; import java.util.* ; public class codeforces_421_A { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int n = sc.nextInt ( ) ; int [ ] arr = new int [ n ] ; for ( int i = 0 ; i < n ; i ++ ) arr [ i ] = sc.nextInt ( ) ; int mn = n + 1 , mx = - 1 ; for ( int i = 0 ; i < n ; i ++ ) { if ( arr [ i ] != i + 1 ) { mn = Math.min ( mn , i ) ; mx = Math.max ( mx , i ) ; } } if ( mx == - 1 ) { System.out.println ( "0 0" ) ; } else { arr = reverse ( arr , mn , mx ) ; if ( check ( arr ) ) System.out.println ( mn + 1 + " " + ( mx + 1 ) ) ; else System.out.println ( "0 0" ) ; } } static boolean check ( int [ ] arr ) { for ( int i = 0 ; i < arr.length ; i ++ ) if ( arr [ i ] != i + 1 ) return false ; return true ; } static int [ ] reverse ( int [ ] arr , int mn , int mx ) { int [ ] res = new int [ arr.length ] ; for ( int i = 0 ; i < arr.length ; i ++ ) res [ i ] = arr [ i ] ; int l = mn , r = mx ; while ( l < r ) { int temp = res [ l ] ; res [ l ] = res [ r ] ; res [ r ] = temp ; l ++ ; r -- ; } return res ; } }

