import java.util.Scanner ; public class atcoder_ABC174_B { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int n = sc.nextInt ( ) ; long mod = 1000000007 ; long [ ] [ ] dp = new long [ n + 1 ] [ 4 ] ; dp [ 0 ] = new long [ 4 ] ; for ( int i = 0 ; i < 4 ; i ++ ) { dp [ 0 ] [ i ] = 1 ; } for ( int i = 1 ; i <= n ; i ++ ) { for ( int j = 0 ; j < 4 ; j ++ ) { dp [ i ] [ j ] = 0 ; for ( int k = 0 ; k < 4 ; k ++ ) { if ( isValid ( i , j , k ) ) { dp [ i ] [ j ] += dp [ i - 1 ] [ k ] ; dp [ i ] [ j ] %= mod ; } } } } long ans = 0 ; for ( int i = 0 ; i < 4 ; i ++ ) { ans += dp [ n ] [ i ] ; ans %= mod ; } System.out.println ( ans ) ; } static boolean isValid ( int i , int j , int k ) { if ( i == 1 ) { if ( j == 0 && k == 0 ) return false ; if ( j == 1 && k == 2 ) return false ; if ( j == 2 && k == 1 ) return false ; if ( j == 3 && k == 3 ) return false ; } if ( i == 2 ) { if ( j == 0 && k == 0 ) return false ; if ( j == 1 && k == 2 ) return false ; if ( j == 2 && k == 1 ) return false ; if ( j == 3 && k == 3 ) return false ; } if ( i == 3 ) { if ( j == 0 && k == 0 ) return false ; if ( j == 1 && k == 2 ) return false ; if ( j == 2 && k == 1 ) return false ; if ( j == 3 && k == 3 ) return false ; } return true ; } }

