import java.util.Scanner ; public class codeforces_522_A { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int t = sc.nextInt ( ) ; int m = sc.nextInt ( ) ; int [ ] arr = new int [ m ] ; int ind = 1 ; while ( t -- > 0 ) { String s = sc.next ( ) ; if ( s.equals ( "alloc" ) ) { int x = sc.nextInt ( ) ; int pos = 0 ; for ( int i = 0 ; i < m ; i ++ ) { if ( arr [ i ] == 0 ) pos ++ ; if ( pos == x ) { for ( int j = i - x + 1 ; j <= i ; j ++ ) arr [ j ] = ind ; System.out.println ( ind ++ ) ; break ; } } if ( pos < x ) System.out.println ( "NULL" ) ; } else if ( s.equals ( "erase" ) ) { int x = sc.nextInt ( ) ; if ( ! contains ( x , arr ) || x == 0 ) System.out.println ( "ILLEGAL_ERASE_ARGUMENT" ) ; else { for ( int i = 0 ; i < m ; i ++ ) if ( arr [ i ] == x ) arr [ i ] = 0 ; } } else { int cnt = 0 ; for ( int i = 0 ; i < m ; i ++ ) if ( arr [ i ] == 0 ) cnt ++ ; arr = remove ( arr ) ; for ( int i = 0 ; i < cnt ; i ++ ) arr.add ( 0 ) ; } } } public static ArrayList < Integer > remove ( ArrayList < Integer > list ) { ArrayList < Integer > newList = new ArrayList < > ( ) ; for ( int i : list ) if ( i != 0 ) newList.add ( i ) ; return newList ; } public static boolean contains ( int x , int [ ] arr ) { for ( int i = 0 ; i < arr.length ; i ++ ) if ( arr [ i ] == x ) return true ; return false ; } }

