import java.util.Scanner ; public class atcoder_ABC171_A { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int H = sc.nextInt ( ) ; int W = sc.nextInt ( ) ; int N = sc.nextInt ( ) ; int s_r = sc.nextInt ( ) ; int s_c = sc.nextInt ( ) ; String S = sc.next ( ) ; String T = sc.next ( ) ; boolean judge = false ; int S_L = 0 ; int S_R = 0 ; int S_U = 0 ; int S_D = 0 ; int T_L = 0 ; int T_R = 0 ; int T_U = 0 ; int T_D = 0 ; for ( int x = 0 ; x < N ; x ++ ) { if ( S.charAt ( x ) == 'L' ) { S_L ++ ; if ( S_L - T_R - s_c == 0 ) { judge = true ; } } else if ( S.charAt ( x ) == 'R' ) { S_R ++ ; if ( s_c + ( S_R - T_L ) == W + 1 ) { judge = true ; } } else if ( S.charAt ( x ) == 'U' ) { S_U ++ ; if ( S_U - T_D - s_r == 0 ) { judge = true ; } } else if ( S.charAt ( x ) == 'D' ) { S_D ++ ; if ( s_r + ( S_D - T_U ) == H + 1 ) { judge = true ; } } if ( T.charAt ( x ) == 'L' ) { if ( S_R - T_L + s_c != 1 ) { T_L ++ ; } } else if ( T.charAt ( x ) == 'R' ) { if ( s_c + ( T_R - S_L ) != W ) { T_R ++ ; } } else if ( T.charAt ( x ) == 'U' ) { if ( S_D - T_U + s_r != 1 ) { T_U ++ ; } } else if ( T.charAt ( x ) == 'D' ) { if ( s_r + ( T_D - S_U ) != H ) { T_D ++ ; } } } if ( judge ) { System.out.println ( "NO" ) ; } else { System.out.println ( "YES" ) ; } } }

