import java.util.Scanner ; public class atcoder_ABC171_A { public static void main ( String [ ] args ) { Scanner stdIn = new Scanner ( System.in ) ; int N = stdIn.nextInt ( ) ; int M = stdIn.nextInt ( ) ; int [ ] [ ] balls = new int [ N ] [ 2 ] ; for ( int i = 0 ; i < N ; i ++ ) { balls [ i ] [ 0 ] = 1 ; balls [ i ] [ 1 ] = 0 ; } balls [ 0 ] [ 0 ] = 0 ; balls [ 0 ] [ 1 ] = 1 ; for ( int i = 0 ; i < M ; i ++ ) { int x = stdIn.nextInt ( ) ; int y = stdIn.nextInt ( ) ; if ( balls [ x - 1 ] [ 0 ] >= 1 && balls [ x - 1 ] [ 1 ] >= 1 ) { balls [ x - 1 ] [ 0 ] -- ; balls [ y - 1 ] [ 1 ] ++ ; } else if ( balls [ x - 1 ] [ 0 ] == 0 ) { balls [ x - 1 ] [ 1 ] -- ; balls [ y - 1 ] [ 1 ] ++ ; } else { balls [ x - 1 ] [ 0 ] -- ; balls [ y - 1 ] [ 0 ] ++ ; } } int ans = 0 ; for ( int i = 0 ; i < N ; i ++ ) { if ( balls [ i ] [ 1 ] > 0 ) { ans ++ ; } } System.out.println ( ans ) ; } }

