import java.util.Scanner ; public class atcoder_ABC171_E { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int N = sc.nextInt ( ) ; int P = sc.nextInt ( ) ; int [ ] a = new int [ N ] ; for ( int i = 0 ; i < N ; i ++ ) { a [ i ] = sc.nextInt ( ) ; } int one = 0 ; int zero = 0 ; for ( int i = 0 ; i < N ; i ++ ) { if ( a [ i ] == 1 ) { one ++ ; } else { zero ++ ; } } long pattern_a = 0 ; long pattern_b = 0 ; for ( int j = 0 ; j <= zero ; j ++ ) { pattern_b += combi ( zero , j ) ; } int time = 0 ; while ( time <= one ) { if ( time % 2 == P ) { pattern_a += combi ( one , time ) ; } time ++ ; } System.out.println ( ( int ) ( pattern_a * pattern_b ) ) ; } public static long combi ( int n , int k ) { long a = 1 ; for ( int i = 0 ; i < k ; i ++ ) { a *= n - i ; a /= i + 1 ; } return a ; } }

