import java.util.Scanner ; public class atcoder_ABC171_A { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int N = sc.nextInt ( ) ; int M = sc.nextInt ( ) ; String S = sc.next ( ) ; String T = sc.next ( ) ; int [ ] Number_iMN = new int [ N ] ; for ( int i = 0 ; i < N ; i ++ ) { Number_iMN [ i ] = i * M / N ; } int [ ] Number_j = new int [ M ] ; for ( int j = 0 ; j < M ; j ++ ) { Number_j [ j ] = j ; } int [ ] Kaburi_i = new int [ M ] ; int [ ] Kaburi_j = new int [ M ] ; int counter = 0 ; int Flag = 0 ; for ( int j = 0 ; j < M ; j ++ ) { for ( int i = 0 ; i < N ; i ++ ) { if ( Number_iMN [ i ] == Number_j [ j ] ) { Kaburi_i [ counter ] = i ; Kaburi_j [ counter ] = j ; counter ++ ; } } } int Kaburi_Size = counter ; while ( counter <= Kaburi_Size - 1 ) { if ( S.charAt ( Kaburi_i [ counter ] ) != T.charAt ( Kaburi_j [ counter ] ) ) { Flag = 1 ; break ; } counter ++ ; } if ( Flag == 1 ) { System.out.println ( - 1 ) ; } else { System.out.println ( N * M / gcd ( N , M ) ) ; } } public static int gcd ( int a , int b ) { while ( b > 0 ) { int temp = b ; b = a % b ; a = temp ; } return a ; } }

